	this.options.graph.trigger('batch:start');
        
        var clone = this.options.cellView.model.clone();
        clone.unset('z');
        this.options.graph.addCell(clone, { halo: this.cid });

        var link = this.options.paper.getDefaultLink(this.options.cellView);

